export class RentalsDatabase<T> implements IEntity<T> {
  protected client = new HttpService();

  constructor(protected url: string) {
    this.url =
      'https://rentals-management-backend.herokuapp.com/api/private/' + url;
  }

  async get() {
    const res = await this.client.get(this.url, axiosConfig).toPromise();
    return res.data;
  }

  getById(id: string) {
    return this.client
      .get<T>(`${this.url}/${id}`, axiosConfig)
      .toPromise()
      .then((res) => res.data);
  }

  create(data: Partial<T>): Promise<T> {
    return this.client
      .post(this.url, data, axiosConfig)
      .toPromise()
      .then((res) => res.data);
  }

  update(id: string, data: Partial<T>): Promise<any> {
    return this.client
      .put(`${this.url}/${id}`, data, axiosConfig)
      .toPromise()
      .then((res) => res.data);
  }

  delete(id: string): Promise<boolean> {
    return this.client
      .delete(`${this.url}/${id}`, axiosConfig)
      .toPromise()
      .then((res) => res.data);
  }
}
